import React, {useEffect, useState} from 'react';
import { useParams, useHistory } from "react-router-dom";
import '../styles/questionsStyle.css';

const QuestionsScreen = () => {
    const [questions, setQuestions] = useState([]);
    const [radioState, setRadioState] = useState(false);
    const history = useHistory();
    let { id } = useParams();

    useEffect(() => {
        setQuestions(history.location.statex.detail);
    },[]);


    useEffect(() => {
        console.log(questions);
    }, [questions]);

    const handleChange = () => {
        setRadioState(!radioState)
    }

    const Item = ({prop}) => {
        return(
          <div>
              <h3>{prop[id].category}</h3>
              <p>{prop[id].question}</p>
              {prop[id].incorrect_answers.map((answer) => (
                  <div>
                      <input key={answer} type="radio" checked={radioState} value={answer}/>
                      <p>{answer}</p>
                  </div>
              ))}
          </div>
        )
    }

    return(
        <div style={{padding: '1em'}}>
            {questions.length!==0 ? <Item prop={questions}/> : null}
        </div>
    )
}

export default QuestionsScreen;
